import React from "react";
import Slider from "react-slick";
import ava01 from '../../assets/images/ava-1.jpg'
import ava02 from '../../assets/images/ava-2.jpg'
import ava03 from '../../assets/images/ava-3.jpg'

const Testimonials = () => {
  return (
    <Slider>
      <div className="testimonial py-4 px-3">
        <p>
          Welcome to our Testimonials section, where our valued travelers share
          their journeys and experiences with us. At Travel World, we believe
          every trip should be memorable, and nothing makes us prouder than
          hearing the stories of joy, adventure, and satisfaction from our
          customers. Their feedback not only inspires us to keep improving but
          also helps future travelers trust us with their dream vacations. Take
          a look at what they have to say!
          <div className="d-flex align-items-center gap-4 mt-3">
            <img src={ava01} className="w-25 h-25 rounded-2" alt="" />
            <div>
                <h5 className="mb-0 mt-3">John </h5>
            </div>
          </div>
        </p>
      </div>
    </Slider>
  );
};

export default Testimonials;
